mapper.select:
select app.id,app.software_name, app.APK_name, dic.value_name as flatFormName, dic2.value_name as statusName, app.status, ca1.categoryName as categoryLevel1Name, app.categoryLevel1, ca2.categoryName as categoryLevel2Name, app.categoryLevel2, ca3.categoryName as categoryLevel3Name, app.categoryLevel3, app.softwareSize, app.downloads, app.versionId, tav.versionNo as versionName from t_data_dictionary dic, t_data_dictionary dic2, t_app_info app join t_app_category ca1 on ca1.id = app.categoryLevel1 join t_app_category ca2 on ca2.id = app.categoryLevel2 join t_app_category ca3 on ca3.id = app.categoryLevel3 join t_app_version tav on tav.id = app.versionId
dic.value_id = app.flatFormId and dic.type_code='APP_FLATFORM' and dic2.type_code='APP_STATUS' and app.status=1 and dic2.value_id=app.status and app.devStatus=0
and dic.value_id = #{map.flatFormId}
and app.software_name like CONCAT('%',#{map.softwareName},'%')
and app.categoryLevel1 = #{map.categoryLevel1}
and app.categoryLevel2 = #{map.categoryLevel2}
and app.categoryLevel3 = #{map.categoryLevel3}
group by app.software_name order by app.modifyDate desc

select distinct count(1) from t_data_dictionary dic, t_data_dictionary dic2, t_app_info app join t_app_category ca1 on ca1.id = app.categoryLevel1 join t_app_category ca2 on ca2.id = app.categoryLevel2 join t_app_category ca3 on ca3.id = app.categoryLevel3 join t_app_version tav on tav.id = app.versionId
dic.value_id = app.flatFormId and dic.type_code='APP_FLATFORM' and dic2.type_code='APP_STATUS' and app.status=1 and app.devStatus=0 and dic2.value_id=app.status
and dic.value_id = #{map.flatFormId}
and app.software_name like concat('%',#{map.softwareName},'%')
and app.categoryLevel1 = #{map.categoryLevel1}
and app.categoryLevel2 = #{map.categoryLevel2}
and app.categoryLevel3 = #{map.categoryLevel3}
order by app.software_name desc

select app.id,app.software_name, app.APK_name, dic.value_name as flatFormName, dic2.value_name as statusName, app.status, ca1.categoryName as categoryLevel1Name, app.categoryLevel1, ca2.categoryName as categoryLevel2Name, app.categoryLevel2, ca3.categoryName as categoryLevel3Name, app.categoryLevel3, app.softwareSize, app.downloads, app.versionId, tav.versionNo as versionName from t_data_dictionary dic, t_data_dictionary dic2, t_app_info app join t_app_category ca1 on ca1.id = app.categoryLevel1 join t_app_category ca2 on ca2.id = app.categoryLevel2 join t_app_category ca3 on ca3.id = app.categoryLevel3 join t_app_version tav on tav.id = app.versionId
dic.value_id = app.flatFormId and dic.type_code='APP_FLATFORM' and dic2.type_code='APP_STATUS' and dic2.value_id=app.status and app.devStatus=0
and app.status = #{map.status}
and dic.value_id = #{map.flatFormId}
and app.software_name like CONCAT('%',#{map.softwareName},'%')
and app.categoryLevel1 = #{map.categoryLevel1}
and app.categoryLevel2 = #{map.categoryLevel2}
and app.categoryLevel3 = #{map.categoryLevel3}
group by app.software_name order by app.modifyDate desc

select distinct count(1) from t_data_dictionary dic, t_data_dictionary dic2, t_app_info app join t_app_category ca1 on ca1.id = app.categoryLevel1 join t_app_category ca2 on ca2.id = app.categoryLevel2 join t_app_category ca3 on ca3.id = app.categoryLevel3 join t_app_version tav on tav.id = app.versionId
dic.value_id = app.flatFormId and dic.type_code='APP_FLATFORM' and dic2.type_code='APP_STATUS' and dic2.value_id=app.status and app.devStatus=0
and app.status = #{map.status}
and dic.value_id = #{map.flatFormId}
and app.software_name like concat('%',#{map.softwareName},'%')
and app.categoryLevel1 = #{map.categoryLevel1}
and app.categoryLevel2 = #{map.categoryLevel2}
and app.categoryLevel3 = #{map.categoryLevel3}
order by app.software_name desc

mapper.update:
update t_app_info
set status = #{appInfo.status}
where id = #{appInfo.appId};
